        paddingTop: RFValue(35),
        paddingLeft: RFValue(15),
        paddingRight: RFValue(paddinrightios),
        alignItems:'center',
    },
    fondo:{
        height: windowHeight,
        width: windowWidth,
        margin:0,
        resizeMode: 'contain',
    },
    logo:{
        resizeMode: 'contain',
        width: windowWidth*0.5,
        height: windowHeight*0.2
    },
    txtbalance:{
        fontSize:RFValue(45),
        color:'white',
        fontWeight:'bold',
    },
    // doscolumnasB:{
    //     flexDirection: 'row',
    //     width: windowWidth,
    // },
    // columnaunoB: {
    //     width:'50%',
    //     alignItems:'flex-end',
    // },
