    //     alignItems:'center',
    //     width:'12%',
    //     borderRadius: 8,
    //     // paddingLeft: '2%',
    //     // paddingRight: '2%',
    // },
    // txtinferiorL:{
    //     fontFamily:'Roboto',
    //     color:'white',
    //     fontSize:RFPercentage(2.5),
    //     fontWeight:'bold',
    //     paddingLeft: '3%',
    //     paddingRight: '3%',
    // },
    // txtinferiorR:{
    //     fontFamily:'Roboto',
    //     color:'white',
    //     fontSize:RFPercentage(2.5),
    //     fontWeight:'bold',
    // },
    dcER:{
        flexDirection: 'row',
        backgroundColor: 'white',
        padding: RFValue(15),
        borderRadius: 10,
        marginTop: RFValue(5),
    },
    dcR:{
        width: '50%',
    },
    dcE:{
        width:'50%',
    },
    btnR:{
        backgroundColor:'#5b298a',
        alignItems:'center',
        marginRight: RFValue(15),
        marginLeft: RFValue(15),
        paddingTop: RFValue(12),
        paddingBottom: RFValue(12),
        borderRadius: 20,
        elevation:24,
        shadowColor: '#000',
        shadowOffset: {width: 0, height: 0},
        shadowOpacity: 0.5,
        shadowRadius: 8,
    },
    textbtnR:{
        color:'white',
        fontWeight: 'bold',
        fontSize:RFValue(11.5),
    },
    balancecry:{
        backgroundColor:'white',
        marginTop: RFValue(15),
        borderTopRightRadius: 20,
        borderTopLeftRadius: 20,
        padding: RFValue(10),
        height: windowHeight*0.7,
    },
    tablacry:{
        marginTop:RFValue(10),
        borderWidth: 0.8,
        borderColor: '#e0e0e0',
        borderRadius:10,
        height: windowHeight*0.08,
        flexDirection:'row',
        paddingLeft: RFValue(10),
        paddingRight: RFValue(12),
    },
    logocry:{
        width:'15%',
        justifyContent: 'center',
    },
    imgcry:{
        width: windowWidth*0.12,
        height: windowHeight*0.12,
        resizeMode: 'contain',
    },
    nombrecry:{
        width:'40%',
        justifyContent: 'center',
        paddingLeft: RFValue(8),
    },
    ntxtcry:{
        fontWeight: 'bold',
        fontSize:RFValue(14),
        color: '#8d8c8c',
        fontFamily: 'opensans-regular'
    },
    smcry:{
        width:'45%',
        alignItems: 'flex-end',
        justifyContent: 'center',
    },
    saldocry:{
        justifyContent: 'center',
        alignItems: 'flex-end',
    },
    stxtcry:{
        fontSize:RFValue(18),
        color: '#8d8c8c',
    },
    monedacry:{
        justifyContent: 'center',
    },
    mtxtcry:{
        fontFamily: 'Roboto',
        fontSize:RFValue(13),
        color: '#8d8c8c',
    },
})
export default Balance
